<?phprequire 'php2console.php';$php_console = new php_console('localhost', '5088');$php_console->output('OMG! AN ERROR!');if ($php_console->output('Confirmd output')){	echo 'Out put worked!';}else{	echo 'O.o it failed. WHY???' . $php_console->last_error;}$acronyms = array('lol', 'lmao', 'omg', 'ttyl', 'brb', 'ianal');$php_console->output('acronyms: ' . $php_console->print_r($acronyms));$php_console->output($acronyms); //If you send only an array, it will out that.$php_console->output_array('acronyms', $acronyms); //out puts the same as $php_console->output('acronyms: ' . $php_console->print_r($acronyms));, looks way neater!?>