<?php//Version 0.1class php_console{	private $ip = null;	private $port = null;	private $fullurl;		public $last_error;	function __construct($ip, $port = 5088)	{		$this->ip = $ip;		$this->port = $port;		$this->fullurl = $ip . ':' . $port;	}		function FileName()	{ 		$filename = explode('/', $_SERVER['PHP_SELF']); 		$filename = array_reverse($filename );		return $filename[0]; 	}		function DoHTTP($URL, $post_fields = array())	{		$returnoutput = array();    		//open connection		$ch = curl_init();		curl_setopt($ch, CURLOPT_URL, $URL);		curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);		//url-ify data for POST		if (count($post_fields) > 0)		{			$fields_string = '';			foreach ($post_fields as $key => $value)			{				$fields_string .= $key . '=' . $value . '&';			}			rtrim($fields_string, '&');			curl_setopt($ch, CURLOPT_POST, count($post_fields));			curl_setopt($ch, CURLOPT_POSTFIELDS, $fields_string);		}		//execute post		$returnoutput['output'] = curl_exec($ch);		//Get status		$returnoutput['status'] = curl_getinfo($ch, CURLINFO_HTTP_CODE);		//close connection		curl_close($ch);		return $returnoutput;	}		//Public	public function print_r($array)	{		return print_r($array, true);	}		public function output($msg)	{		if (is_array($msg))		{			$msg = $this->print_r($msg);		}				$node_result = $this->DoHTTP($this->fullurl . '/?type=output' , array('out' => $msg, 'date' => date("h:i:s A"), 'script' => $this->FileName()));				if ($node_result['status'] == 200)		{			$node_data = json_decode($node_result['output'], true);			if (isset($node_data['success']) && $node_data['success'] == 'true')			{				return true;			}			else			{				$this->last_error = 'HTTP ERROR CODE: ' . $node_data['error'];				return false;			}		}		else		{			$this->last_error = 'HTTP ERROR CODE: ' . $node_result['status'];			return false;		}	}		public function output_array($title, $array)	{		return $this->output($title . ': ' . $this->print_r($array));	}}?>